def commonise(string):
    for x in string.split():
        if x == None:
            pass
        else:
            x = TextBlob(x)
            if x.tags != []:
                if x.tags[0][1] == 'NN' or x.tags[0][1] == 'JJ':
                    array.append(x.tags[0][0])
                    
            else:
                pass

    return array


        
        
